    var obj = msg.payload[i];
    var idx = "["+obj[0]+","+obj[1]+","+obj[2]+","+obj[3]+","+obj[4]+","+obj[5]+"]";

    var oname = whinfo[idx+".name"];
    var otype = whinfo[idx+".type"];

    if ((oname !== undefined) && (otype !== undefined)) {
        nmsg = {};
        nmsg.topic = msg.topic + "/" + oname;
        switch (otype) {
            case "TEMP":
                var temp;
                if (obj[7] < 128) {
                    temp = (obj[6] + obj[7]*256) / 10;
                } else {
                    temp = ((obj[6] + obj[7]*256)-65535) / 10;
                }
                nmsg.payload = temp;
                break;
            case "VALUE":
                var value;
                if (obj[7] < 128) {
                    value = (obj[6] + obj[7]*256);
                } else {
                    value = (obj[6] + obj[7]*256)-65535;
                }
                nmsg.payload = value;
                break;
        }
        if ((oname == "Betriebsart") && (value > 10)) {
            // Zeitprogramm merken zum setzen via Betriebsart/set = auto
            flow.set('zeitprogramm',value);
        }
        // node.warn(nmsg)
        node.send(nmsg);
    }
}

